changeSet.sql:
CREATE TABLE account ( id BIGSERIAL PRIMARY KEY, name TEXT NOT NULL, active BOOLEAN NOT NULL DEFAULT TRUE, created_at TIMESTAMPTZ NOT NULL DEFAULT now(), updated_at TIMESTAMPTZ );
CREATE UNIQUE INDEX ux_account_name_ci ON account (LOWER(name));
CREATE INDEX idx_account_active ON account (active);
CREATE TABLE account_connection ( id BIGSERIAL PRIMARY KEY, account_id BIGINT NOT NULL REFERENCES account (id) ON DELETE CASCADE, marketplace TEXT NOT NULL, active BOOLEAN NOT NULL DEFAULT TRUE, masked_credentials TEXT, last_sync_at TIMESTAMPTZ, last_sync_status TEXT, created_at TIMESTAMPTZ NOT NULL DEFAULT now(), updated_at TIMESTAMPTZ, CONSTRAINT uq_account_marketplace UNIQUE (account_id, marketplace) );
CREATE INDEX idx_acc_conn_account ON account_connection (account_id);
CREATE INDEX idx_acc_conn_marketplace ON account_connection (marketplace);
