changeSet.sql:
-- ================================ -- ETL SYNC AUDIT -- ================================ CREATE TABLE etl_sync_audit ( id BIGSERIAL PRIMARY KEY, request_id VARCHAR(64) NOT NULL, account_id BIGINT NOT NULL, event VARCHAR(64) NOT NULL, date_from DATE NOT NULL, date_to DATE NOT NULL, status VARCHAR(32) NOT NULL, failed_sources TEXT NULL, created_at TIMESTAMPTZ NOT NULL DEFAULT now() );
CREATE INDEX IF NOT EXISTS idx_etl_sync_audit_req ON etl_sync_audit (request_id);
CREATE INDEX IF NOT EXISTS idx_etl_sync_audit_acc_created ON etl_sync_audit (account_id, created_at DESC);
