CREATE TABLE dim_warehouse_ozon ( warehouse_id BIGSERIAL PRIMARY KEY, warehouse_type VARCHAR(32) NOT NULL, name TEXT NOT NULL, is_active BOOLEAN NOT NULL DEFAULT TRUE, created_at TIMESTAMPTZ NOT NULL DEFAULT now(), updated_at TIMESTAMPTZ NOT NULL DEFAULT now() );
CREATE INDEX IF NOT EXISTS idx_dim_warehouse_ozon_type ON dim_warehouse_ozon (warehouse_type);
CREATE TABLE dim_warehouse_wb ( warehouse_id BIGSERIAL PRIMARY KEY, warehouse_type VARCHAR(32) NOT NULL, name TEXT NOT NULL, is_active BOOLEAN NOT NULL DEFAULT TRUE, created_at TIMESTAMPTZ NOT NULL DEFAULT now(), updated_at TIMESTAMPTZ NOT NULL DEFAULT now() );
CREATE INDEX IF NOT EXISTS idx_dim_warehouse_wb_type ON dim_warehouse_wb (warehouse_type);
