changeSet.sql:
CREATE TABLE IF NOT EXISTS dim_tariff ( id BIGSERIAL PRIMARY KEY,
source_platform TEXT NOT NULL,
dim_category_id BIGINT NOT NULL,
kgvp_booking NUMERIC(9, 4) NOT NULL, kgvp_marketplace NUMERIC(9, 4) NOT NULL, kgvp_pickup NUMERIC(9, 4) NOT NULL, kgvp_supplier NUMERIC(9, 4) NOT NULL, kgvp_supplier_express NUMERIC(9, 4) NOT NULL, paid_storage_kgvp NUMERIC(9, 4) NOT NULL,
valid_from DATE NOT NULL DEFAULT CURRENT_DATE, valid_to DATE NULL,
created_at TIMESTAMPTZ NOT NULL DEFAULT now(), updated_at TIMESTAMPTZ NOT NULL DEFAULT now(),
CONSTRAINT fk_dim_tariff_category FOREIGN KEY (dim_category_id) REFERENCES dim_category (id),
CONSTRAINT uq_dim_tariff_source UNIQUE (source_platform, dim_category_id, valid_from) );
CREATE INDEX IF NOT EXISTS idx_dim_tariff_category ON dim_tariff (source_platform, dim_category_id);
CREATE INDEX IF NOT EXISTS idx_dim_tariff_valid_period ON dim_tariff (valid_from, valid_to);
