changeSet.sql:
create table if not exists dim_product_wb ( id bigserial primary key,
nm_id bigint not null, imt_id bigint,
vendor_code text,
subject_id bigint, subject_name text,
brand text, title text,
created_at timestamptz not null default now(), updated_at timestamptz not null default now(),
constraint uq_dim_product_wb_nm unique (nm_id) );
create index if not exists idx_dim_product_wb_imt on dim_product_wb (imt_id);
create index if not exists idx_dim_product_wb_subject on dim_product_wb (subject_id);
