changeSet.sql:
CREATE TABLE IF NOT EXISTS warehouse ( id BIGSERIAL PRIMARY KEY,
marketplace VARCHAR(32) NOT NULL, mp_warehouse_id VARCHAR(64), name VARCHAR(256) NOT NULL, region VARCHAR(256), type VARCHAR(32),
last_synced_at TIMESTAMPTZ,
UNIQUE (marketplace, mp_warehouse_id, name) );
CREATE INDEX IF NOT EXISTS idx_warehouse_marketplace ON warehouse (marketplace);
CREATE INDEX IF NOT EXISTS idx_warehouse_mp_id ON warehouse (marketplace, mp_warehouse_id);
