changeSet.sql:
create table if not exists datapulse.fact_finance ( id bigserial primary key,
account_id bigint not null, source_platform text not null, order_id text not null, finance_date date not null,
currency text not null default 'RUB',
revenue_gross numeric(18, 2) not null default 0, seller_discount_amount numeric(18, 2) not null default 0, marketplace_commission_amount numeric(18, 2) not null default 0, acquiring_commission_amount numeric(18, 2) not null default 0, logistics_cost_amount numeric(18, 2) not null default 0, penalties_amount numeric(18, 2) not null default 0, marketing_cost_amount numeric(18, 2) not null default 0, compensation_amount numeric(18, 2) not null default 0, refund_amount numeric(18, 2) not null default 0, net_payout numeric(18, 2) not null default 0,
created_at timestamptz not null default now(), updated_at timestamptz not null default now(),
constraint uq_fact_finance_business_key unique (account_id, source_platform, order_id, finance_date) );
create index if not exists ix_fact_finance_account_platform_date on datapulse.fact_finance (account_id, source_platform, finance_date);
create index if not exists ix_fact_finance_order_id on datapulse.fact_finance (order_id);
